import { useState } from "react"
import SubmitData from "./SubmitData"
const UserDetails=(props)=>{
    const [username,setusername]=useState('')
    const [age,setuserage]=useState('')
    const handleFormSubmit=(event)=>{
        event.preventDefault()
        const user= document.getElementById('username')
        const ages = document.getElementById('age')
        if(user.value.trim().length===0){
            console.log("lengthissmall")
            return 
        }
        else if(parseInt(ages.value)<0){
            console.log('ageisnegaive')
            return
        }
        else{
            setusername(user.value)
            setuserage(ages.value)
        }

    }
    const usernamefunc=(event)=>{
        setusername(event.target.value)
        event.preventDefault()
        if(username.trim().length===0){
            return 
        }



    }
    const agefunc=(event)=>{
        setuserage(event.target.value)
        event.preventDefault()
        const age_check =parseInt((event.target.value))
        if(age<0){
            return
        }

    }

    return(
        
        <form onSubmit={handleFormSubmit}>
            <div>
                <label for='username' >Username</label>
                    <input id='username' type='text' onChange={usernamefunc}/>
                <label for='age'>Age</label>
                <input id='age' type='number' onChange={agefunc}/>
                <button type='submit' >Submit Age</button>
            </div>
            <div>
                <SubmitData name={username}
                age={age}/>
            </div>
        </form>
    )
}
