import { useState } from "react"
import SubmitData from "./SubmitData"
import './Form.css'
const UserDetails=()=>{

    const [username,setusername]=useState('')
    const [age,setuserage]=useState('')
    const [totaldata,settotaldata]=useState([])
    const handleFormSubmit=(event)=>{
        event.preventDefault()
        if(username.trim().length===0){
            console.log("lengthissmall")
            return 
        }
        else if(parseInt(age)<0){
            console.log('ageisnegaive')
            return
        }
        const newdata = {username,age}
        settotaldata([...totaldata,newdata])

    }
    const usernamefunc=(event)=>{
        setusername(event.target.value)




    }
    const agefunc=(event)=>{
        setuserage(event.target.value)


    }
    // const [formdata,setformdata]=useState({username:'',age:''})
    
    

    return(
        
        <form onSubmit={handleFormSubmit}>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label for='username' >Username</label>
                    <input id='username' value={username} type='text' onChange={usernamefunc} value={username}/>
                    <label for='age'>Age</label>
                    <input id='age' type='number' onChange={agefunc} value={age}/>
                    <button type='submit' >Submit Age</button>
                </div>

            </div>
            <div>
                {totaldata.map((ele, i) => (
                    <SubmitData name={ele.username}
                    age={ele.age}/>
                ))}
                
                
            </div>
        </form>
    )
}
export default UserDetails
