import { useState } from "react"
import SubmitData from "./SubmitData"
const UserDetails=(props)=>{
    const [username,setusername]=useState('')
    const [age,setuserage]=useState('')
    const handleFormSubmit=(event)=>{
        event.preventDefault()
        const user = document.getElementById('username')
        const age = document.getElementById('age')
        setusername(user.value )
        setuserage(age.value)
        if(username.trim().length<0 ||parseInt(age)==0 ){
            return
        }
        user.value=''
        age.value=''
        console.log('insidehandleform',event.target.value)

    }
    const usernamefunc=(event)=>{
        console.log('im in usernmaefunc')
        console.log(event.target.value,'eventtargetvalue')


    }
    const agefunc=(event)=>{
        const age_check =parseInt((event.target.value))
        if(age_check<0){
            console.log('agecheck',typeof(age_check))
            return
        }

    }

    return(
        
        <form onSubmit={handleFormSubmit}>
            <div>
                <label for='username' >Username</label>
                    <input id='username' type='text' onChange={usernamefunc}/>
                <label for='age'>Age</label>
                <input id='age' type='number' onChange={agefunc}/>
                <button type='submit' >Submit Age</button>
            </div>
            <div>
                <SubmitData name={username}
                age={age}/>
            </div>
        </form>
    )
}
export default UserDetails
