import React, { useState } from "react";
import SubmitData from "./SubmitData";

const UserDetails = (props) => {
    const [username, setUsername] = useState('');
    const [age, setAge] = useState('');

    const handleFormSubmit = (event) => {
        event.preventDefault();

        // Validate input
        if (username.trim().length === 0 || parseInt(age) < 0) {
            alert("Please enter a valid username and age.");
            return;
        }

        // Proceed with form submission logic
        console.log("Submitted username:", username);
        console.log("Submitted age:", age);

        // Optionally, you can clear the form fields after submission
        setUsername('');
        setAge('');
    };

    const usernamefunc = (event) => {
        setUsername(event.target.value);
    };

    const agefunc = (event) => {
        setAge(event.target.value);
    };

    return(
        <form onSubmit={handleFormSubmit}>
            <div>
                <label htmlFor='username'>Username</label>
                <input id='username' type='text' onChange={usernamefunc} value={username} />
                <label htmlFor='age'>Age</label>
                <input id='age' type='number' onChange={agefunc} value={age} />
                <button type='submit'>Submit Age</button>
            </div>
            <div>
                <SubmitData name={username} age={age} />
            </div>
        </form>
    );
};

export default UserDetails;
