        <form onSubmit={handleFormSubmit}>
            <div>
                <label for='username' >Username</label>
                    <input id='username' type='text' />
                <label for='age'>Age</label>
                <input id='age' type='number' />
                <button type='submit' >Submit Age</button>
            </div>
            <div>
                <SubmitData name={username}
                age={age}/>
            </div>
        </form>
    )
}
