import React, { useState } from "react";
import SubmitData from "./SubmitData";

const UserDetails = () => {
    const [username, setUsername] = useState('');
    const [age, setAge] = useState('');
    const [totalData, setTotalData] = useState([]);

    const handleFormSubmit = (event) => {
        event.preventDefault();
        if (username.trim().length === 0) {
            console.log("length is small");
            return;
        } else if (parseInt(age) < 0 || isNaN(parseInt(age))) {
            console.log('age is negative or not a number');
            return;
        }
        
        // Create a new data object
        const newData = { username, age };
        
        // Update the state with the new data
        setTotalData([...totalData, newData]);
        
        // Reset form fields
        setUsername('');
        setAge('');
    };

    const usernameFunc = (event) => {
        setUsername(event.target.value);
    };

    const ageFunc = (event) => {
        setAge(event.target.value);
    };

    return (
        <form onSubmit={handleFormSubmit}>
            <div>
                <label htmlFor='username'>Username</label>
                <input id='username' type='text' onChange={usernameFunc} value={username} />
                <label htmlFor='age'>Age</label>
                <input id='age' type='number' onChange={ageFunc} value={age} />
                <button type='submit'>Submit Age</button>
            </div>
            <div>
                {totalData.map((ele, i) => (
                    <SubmitData key={i} name={ele.username} age={ele.age} />
                ))}
            </div>
        </form>
    );
};

export default UserDetails;
