        setisvisble(false)

    }
    console.log('iam in layout')
    return(
        <div>
            {!isvisible && (
                <div>
                    <h2>Please enter valid data</h2>
                    <button type='submit' onClick={errorHandler}>Okay</button>  
                </div>      

            )}

                  

        </div>
    )
}
export default Layout
