        console.log('iam inhandleformsubmit',user.value)
        const age = document.getElementById('age')
        setusername(user+age)
        
        return (
            <h2>username</h2>
        )
            
        


    }
    const usernamefunc=(event)=>{
        console.log(event.target.value,'eventtargetvalue')

    }
    return(
        <form hhandleformsubmit>
            <div>
                <label for='username' onChange={usernamefunc}>Username</label>
