import { useState } from "react"
import SubmitData from "./SubmitData"
const UserDetails=(props)=>{
    const [username,setusername]=useState('')
    const handleFormSubmit=(event)=>{
        event.preventDefault()
        const user = document.getElementById('username')
        console.log('iam inhandleformsubmit',user.value)
        const age = document.getElementById('age')
        setusername(user.value + '('+ age.value + ')')

    }
    const usernamefunc=(event)=>{
        console.log(event.target.value,'eventtargetvalue')

    }
    console.log(username,'username')
    return(
        
        <form >
            <div>
                <label for='username' onChange={usernamefunc}>Username</label>
                    <input id='username' type='text'/>
                <label for='age'>Age</label>
                <input id='age' type='number'/>
                <button type='submit' onClick={handleFormSubmit}>Submit Age</button>
            </div>
            <div>
                <SubmitData props={username}/>
            </div>
        </form>
    )
}
export default UserDetails
