import { useState } from "react";
import SubmitData from "./SubmitData";

const UserDetails = (props) => {
  const [username, setUsername] = useState('');
  const [age, setAge] = useState('');

  const handleFormSubmit = (event) => {
    event.preventDefault();
    // Update the state only when the submit button is clicked
    // This prevents the UI from updating on every keystroke
    setFormData({
      username,
      age
    });
  };

  const usernamefunc = (event) => {
    setUsername(event.target.value);
  };

  const agefunc = (event) => {
    setAge(event.target.value);
  };

  const [formData, setFormData] = useState({
    username: '',
    age: ''
  });

  return (
    <form onSubmit={handleFormSubmit}>
      <div>
        <label htmlFor='username'>Username</label>
        <input id='username' type='text' onChange={usernamefunc} value={username} />
        <label htmlFor='age'>Age</label>
        <input id='age' type='number' onChange={agefunc} value={age} />
        <button type='submit'>Submit Age</button>
      </div>
      <div>
        {/* Pass formData to SubmitData component */}
        <SubmitData name={formData.username} age={formData.age} />
      </div>
    </form>
  );
};

export default UserDetails;
